import{_ as d,c as r,a2 as a,o as e}from"./chunks/framework.BW-ZVgUE.js";const p=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"Study/前端测试总结.md","filePath":"Study/前端测试总结.md"}'),l={name:"Study/前端测试总结.md"};function o(h,t,i,b,n,c){return e(),r("div",null,t[0]||(t[0]=[a('<h2 id="一、功能测试" tabindex="-1">一、功能测试 <a class="header-anchor" href="#一、功能测试" aria-label="Permalink to &quot;一、功能测试&quot;">​</a></h2><ol><li><p>输入框</p><p>输入框是测试中最容易出现Bug的地方，所以在测试时，一定要重点注意。</p></li></ol><table tabindex="0"><thead><tr><th>一般情况</th><th>描述</th></tr></thead><tbody><tr><td>汉字</td><td></td></tr><tr><td>数字</td><td>最大值,最小值,位数限制</td></tr><tr><td>空</td><td></td></tr><tr><td>特殊字符</td><td></td></tr><tr><td>字数限制考虑，超长或为空的考虑</td><td></td></tr><tr><td>空格、回车、Tab</td><td></td></tr><tr><td>字符前有空格</td><td></td></tr><tr><td>字符后有空格</td><td></td></tr><tr><td>字符前后都有空格</td><td></td></tr><tr><td>多行文本框输入</td><td>是否支持回车换行</td></tr><tr><td>使用复制粘贴功能尝试输入时</td><td></td></tr></tbody></table><table tabindex="0"><thead><tr><th>安全性检查</th><th>描述</th></tr></thead><tbody><tr><td>特殊字符串</td><td><code>null</code>、<code>NULL</code>、 、<code>document.write(&quot;123&quot;)</code>、<code>&lt;script&gt;&lt;/script&gt;</code>、<code>&lt;iframe&gt;&lt;/iframe&gt;</code>等</td></tr></tbody></table><table tabindex="0"><thead><tr><th>特殊情况</th><th>描述</th></tr></thead><tbody><tr><td>敏感词处理</td><td>非必须</td></tr></tbody></table><ol start="2"><li>图片上传处理</li></ol><table tabindex="0"><thead><tr><th>一般情况</th><th>描述</th></tr></thead><tbody><tr><td>图片大小限制</td><td></td></tr><tr><td>图片类型限制</td><td>上传类型不正确的拦截处理</td></tr></tbody></table><ol start="3"><li>用户请求</li></ol><table tabindex="0"><thead><tr><th>一般情况</th><th>描述</th></tr></thead><tbody><tr><td>防抖节流处理</td><td></td></tr><tr><td>网速慢的情况考虑</td><td>请求失败的提示</td></tr><tr><td>用户及时反馈信息以及请求失败撤销结果</td><td></td></tr><tr><td>添加数据时,字段是否唯一</td><td>添加重复数据的情况</td></tr><tr><td>添加含有空格或者特殊字符的内容</td><td></td></tr><tr><td>修改/删除/查询不存在的信息时的情况</td><td></td></tr><tr><td>修改数据时,字段是否唯一</td><td>修改数据为相同字段的情况,前后添加空格的情况</td></tr><tr><td>查询数据时，如果有重复数据是否去重</td><td></td></tr></tbody></table><table tabindex="0"><thead><tr><th>登录注册</th><th>描述</th></tr></thead><tbody><tr><td>用户名密码去除空格</td><td>也许只是字符前后的空格</td></tr><tr><td>是否区分大小写</td><td></td></tr><tr><td>用户名密码的字数限制</td><td></td></tr><tr><td>已经登录过的用户是否跳转首页</td><td></td></tr><tr><td>注册成功时，用户是否以登录状态跳转到首页</td><td></td></tr><tr><td>退出登录时，是否保留cookie</td><td></td></tr></tbody></table><ol start="4"><li><p>直接URL链接检查</p><p>直接输入各功能页面URL地址,看系统如何处理,是否会报错.</p></li><li><p>网络</p><p>在测试时,有与网络有关的步骤必须考虑到网速慢,或者断网的情况.</p></li><li><p>并发情况考虑</p></li></ol><table tabindex="0"><thead><tr><th>一般情况</th><th>描述</th></tr></thead><tbody><tr><td>当某个资源被删除时</td><td>如果发送的请求需要依赖这个资源,需要检查是该资源否存在</td></tr><tr><td>多个用户同时操作同一个数据时</td><td></td></tr></tbody></table><h2 id="二、界面和易用性测试" tabindex="-1">二、界面和易用性测试 <a class="header-anchor" href="#二、界面和易用性测试" aria-label="Permalink to &quot;二、界面和易用性测试&quot;">​</a></h2><ol><li>主要观察网站的界面是否和设计稿一致，是否有错别字，页面布局是否合理，是否有相应的错误提示信息等。</li><li>主要是考察交互是否人性化，是否易用，是否符合大多数用户的使用习惯等。</li></ol><h2 id="三、兼容性测试" tabindex="-1">三、兼容性测试 <a class="header-anchor" href="#三、兼容性测试" aria-label="Permalink to &quot;三、兼容性测试&quot;">​</a></h2><p>界面在不同操作系统或浏览器下的兼容情况，以及不同功能方面的测试，也需要考虑到不同浏览器下的兼容问题。</p><h2 id="四、业务流程测试" tabindex="-1">四、业务流程测试 <a class="header-anchor" href="#四、业务流程测试" aria-label="Permalink to &quot;四、业务流程测试&quot;">​</a></h2><p>业务流程，一般会涉及到多个模块的数据，所以在对业务流程测试时，首先要保证单个模块功能的正确性，其次就要对各个模块间传递的数据进行测试，这往往是容易出现问题的地方，测试时一定要设计不同的数据进行测试。</p><h2 id="五、浏览器相关测试" tabindex="-1">五、浏览器相关测试 <a class="header-anchor" href="#五、浏览器相关测试" aria-label="Permalink to &quot;五、浏览器相关测试&quot;">​</a></h2><p>浏览器的前进后退，刷新按钮。</p><h2 id="六、安全性测试" tabindex="-1">六、安全性测试 <a class="header-anchor" href="#六、安全性测试" aria-label="Permalink to &quot;六、安全性测试&quot;">​</a></h2><ol><li>SQL注入： SQL注入即是指<strong>web应用程序对用户输入数据的合法性没有判断或过滤不严</strong>，攻击者可以在web应用程序中事先定义好的查询语句的结尾上添加额外的SQL语句，在管理员不知情的情况下实现非法操作，以此来实现欺骗数据库服务器执行非授权的任意查询，从而进一步得到相应的数据信息。</li><li>XSS跨网站脚本攻击：程序或数据库没有对一些特殊字符进行过滤或处理，导致用户 所输入的一些破坏性的脚本语句能够直接写进数据库中，浏览器会直接执行这些脚本语句，破坏网站的正常显示，或网站用户的信息被盗,构造脚本语句时，要保证脚本的完整性。</li></ol>',22)]))}const u=d(l,[["render",o]]);export{p as __pageData,u as default};
